  // Create a new anchor element
var link = document.createElement("a");

// Set the href attribute to the URL you want to redirect to
link.href = "https://evil.com";

// Set the text content of the anchor element
link.textContent = "Click me to redirect";

// Append the anchor element to the document body or any other desired location
document.body.appendChild(link);

// Add an event listener to the anchor element to handle the click event
link.addEventListener("click", function(event) {
    // Prevent the default action of the click event (i.e., following the link)
    event.preventDefault();

    // Perform the redirection manually
    window.location.href = link.href;
});
